    function get_link($relative_route, $language) {
      $relative_route = str_replace("$language/", '', $relative_route);
      $link = $GLOBALS['base_url']."/";
      $link .= substr($language, 0, 2);
      $link .= "/".drupal_get_path_alias($relative_route, $language);
      return $link;
    }

