    function setImagesSlider() {
      tamano = $(".views-field-field-jquery-home-mobile .field-content img").length;
      if (tamano > 0) {
        contador = 0;
        while (contador < (tamano)) {
          $(".views-field-field-jquery-home-mobile .field-content img:eq(" + contador + ")").css("display", "none");
          valor_imagen = $(".views-field-field-jquery-home-mobile .field-content img:eq(" + contador + ")").attr('src');
          console.log(valor_imagen);
          contador++;
          $(".field-slideshow-slide-" + contador).append('<img class="slider-movil-' + contador + '" src="' + valor_imagen + '">');
        }
      }
    }

    function setSliderImages() {
      var wViewport = $(window).width();
      var hViewport = $(window).height();

      $('body.front #header div.section').css({
        'width': '100%',
        'max-width': wViewport + '!important',
        'overflow-x': 'hidden'
      });

      if (wViewport < 768 && wViewport < hViewport) {
        $('.field-slideshow-wrapper').removeClass('sliderPortrait');
        $('.field-slideshow-wrapper').removeClass('sliderlandscape');
        $('.field-slideshow-wrapper').addClass('sliderPortrait');

      } else {
        $('.field-slideshow-wrapper').removeClass('sliderPortrait');
        $('.field-slideshow-wrapper').removeClass('sliderlandscape');
        $('.field-slideshow-wrapper').addClass('sliderlandscape');
      }
    }
    setTimeout(setImagesSlider, 300);
    setTimeout(setSliderImages, 300);
    $(window).resize(function() {
      setSliderImages();
    });

  });
